import './acceuil.css'

import axios from 'axios';

import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css'; // Import css
// import Modal from './modal';
export default class Acceuil extends Component {
    constructor(props) {
        super(props);

        this.state = {
            profil: [],
            nom:"",
            prenom:"",
            email:"",
            telephone:""
        };
        this.handleChange = this.handleChange.bind(this);

    }
    componentDidMount() {

        axios.get("http://localhost:8080/book4.json").then(res => {

            this.setState({ profil: res.data })
            console.log(this.state.profil)

        })
    }
    handleChange(e){
        this.setState({
            [e.target.name]: e.target.value
        })
    }
    render() {


        return (

              <div id="DD">
            <div id="descript">
                <div>
                    <div>
                        <span>Filter by:</span>
                    <select onChange={this.handleChange}name="select" id="select">
                        <option value="tout" >Tout</option>
                        <option value="horror" >horror</option>
                        <option value="Fantastic">Fantastic</option>
                    </select>
                    </div>

                </div>
           {this.state.profil.length>0 ?(this.state.profil.filter(a=>{
               if(document.getElementById("select").options[document.getElementById("select").selectedIndex].value=="tout"){
                   return a
               }else{
                return a.genre===document.getElementById("select").options[document.getElementById("select").selectedIndex].value
               }
               }).map(prof=>{
            console.log(prof)

      return(
  <div id="atelier"> 
      <div class=" cardbody card-body">
             <p class="card-text"><strong>Title :</strong> {prof.name}</p>
             <p class="card-text"><strong>Author:</strong>{prof.author.name}</p>
             <p class="card-text"><strong>Genre :</strong>{prof.genre}</p>
             <p class="card-text"><strong>Publish at  :</strong>{prof.date}</p>


  </div > 

     </div>
      )})):""} 
           </div>

    </div>


        )
    }
}
