import React, { Component } from 'react'
import axios from 'axios';
import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css'; // Import css
export default class MonAtelier extends Component {
    constructor(props) {
        super(props);
        this.state = {
            nom:"",
            titre:"",
            description:"",
            horaire:"",
            duree:"",
            date:"",
            placedispo:"",
            placereserve:"",
            prix:"",
            image: '',
            redirect:false,
            atelier:[],
            name:""
        };

        this.handleChange = this.handleChange.bind(this);

    }


    handleChange(e){
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    componentDidMount(){

        this.setState({name:localStorage.getItem('jwtToken')})
        console.log(localStorage.getItem('jwtToken'))
        axios.get('http://localhost:8080/cuisinier/'+ localStorage.getItem('id')).then(res=>{
            console.log(res.data)
            this.setState({atelier:res.data})
        })

    }
    render() {
        return (
            <div>
                           {this.state.atelier.length>0 ?(this.state.atelier.map(prof=>{
                var url="http://localhost:8080/public/"+prof.image
 return (
    <div className="container">
        <div className="row"> 
         <div class="col-md-4">
            <div class="card-body">
             <img src={url} id="larg"/>
             <h5 class="card-title">{prof.titre}</h5>
             <p class="card-text">{prof.description}</p>
             <p class="card-text">{prof.date}</p>
             <p class="card-text">{prof.horaire}</p>
             <p class="card-text">{prof.placedispo}</p>
             <p class="card-text">{prof.placereserve}</p>
             <p> {prof.prix }</p><br></br>
               <div>
         {prof.visibilite==true ?(<button onClick={(e)=>{
             e.preventDefault()
            axios.get(" http://localhost:8080/atelieraffichier/"+prof._id).then(res=>{
                 axios.get('http://localhost:8080/cuisinier/'+ localStorage.getItem('id')).then(res=>{
            console.log(res.data)
            this.setState({atelier:res.data})
        })
                console.log(res.data)})


         }}>Desactiver</button>):(<button onClick={(e)=>{
            e.preventDefault()
            console.log(prof._id)
           axios.get("http://localhost:8080/atelieraffichier/"+prof._id).then(res=>{
  axios.get('http://localhost:8080/cuisinier/'+ localStorage.getItem('id')).then(res=>{
            console.log(res.data)
            this.setState({atelier:res.data})
        })
                console.log(res.data)})

         }}>Activer</button>)}
                </div>
         <button  onClick={()=>{
                //  axios.post("http://localhost:8080/particulier/"+prof._id)
                confirmAlert({
                    customUI: ({ onClose }) => {
                      return (
                        <div className='custom-ui'>
                         <h5 class="card-title">Modification Atelier</h5>
                        <input  name="titre" onChange={ this.handleChange } placeholder="Entrer votre titre" value={this.state.value}/><br/>
                        <input name="description" onChange={ this.handleChange }  placeholder="Entrer votre description"v alue={this.state.value}/><br/>
                        <input name="date" onChange={ this.handleChange } placeholder="Entrer votre email" value={this.state.value} type="date"/><br/>
                        <input name="horaire" onChange={ this.handleChange } placeholder="Entrer votre horaire"/><br/>
                        <input  name="placedispo" onChange={ this.handleChange } placeholder="Entrer place disponible" type="Number" value={this.state.value}/><br/>
                        <input name="placereserve" onChange={ this.handleChange }  placeholder="Entrer place reserve"type="Number" alue={this.state.value}/><br/>
                        <input name="prix" onChange={ this.handleChange } placeholder="Entrer prix" type="Number" value={this.state.value}/><br/>
                        <input name="telephone"  ref={(ref) => { this.uploadInput2 = ref; }} type="file"/><br/>
                          <button onClick={onClose}>Fermer</button>
                          <button
                            onClick={() => {
                                const data2 = new FormData();
        data2.append('image', this.uploadInput2.files[0]);
        data2.append('titre', this.state.titre);
        data2.append('description', this.state.description);
        data2.append('date', this.state.date);
        data2.append('horaire', this.state.horaire);
        // data.append('duree',  this.state.duree);
        data2.append('placedispo', this.state.placedispo);
        data2.append('placereserve', this.state.placereserve);
        data2.append('prix', this.state.prix);
        data2.append('duree',  this.state.duree);

        fetch('http://localhost:8080/atelier/'+prof._id, {
            method: 'POST',
            body: data2,
        }).then((response) => {
            response.json().then((body) => {

                console.log(body.file1)
                //  this.setRedirect()
                axios.get('http://localhost:8080/cuisinier/'+ localStorage.getItem('id')).then(res=>{
            console.log(res.data)
            this.setState({atelier:res.data})
        })

            });
            onClose();
        });




                            }}
                          className="btn btn-success">
                          Modifier
                          </button>
                        </div>
                      );
                    }
                  });
                 }}>Modifier</button>

            </div>
         </div>

        </div>
    </div>

        )
            }

              )):""} 
            </div>
        )
    }
}
