import React, { Component } from 'react'
import './acceuil.css'

import axios from 'axios';

import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css'; // Import css
// import Modal from './modal';
export default class Acceuil extends Component {
    constructor(props) {
        super(props);

        this.state = {
            profil: [],
            nom:"",
            prenom:"",
            email:"",
            telephone:""
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleChange2 = this.handleChange2.bind(this);
    }
    componentDidMount() {

        axios.get("http://localhost:8080/book6.json").then(res => {
            if(document.getElementById("select3").selectedIndex==0){
                console.log(document.getElementById("select3").selectedIndex)

                res.data.sort((a,b)=>{
                    var x=a.name.toLowerCase()
                    var y=b.name.toLowerCase()
                    if(x<y){
                        return -1
                    }
                     if(x>y){
                         return 1
                     }
                     return 0
                })
            }
          else if(document.getElementById("select3").selectedIndex==1){
            console.log(document.getElementById("select3").selectedIndex)

                res.data.sort((a,b)=>{
                    var x=a.author.name.toLowerCase()
                    var y=b.author.name.toLowerCase()
                    if(x<y){
                        return -1
                    }
                     if(x>y){
                         return 1
                     }
                     return 0
                })
            }
            console.log(res.data)
   this.setState({ profil: res.data })
        })
    }
    handleChange(e){
        this.setState({
            [e.target.name]: e.target.value
        })
    }
    handleChange2(e){
        this.setState({
            [e.target.name]: e.target.value
        })
    }
    render() {


        return (

              <div id="DD">
            <div id="descript">
                <div>
                    <div>
                    <p>Sort by</p> 
                    <select onChange={this.handleChange}name="select3" id="select3">
                        <option value="name" >Name</option>
                        <option value="name1">author name</option>
                    </select>

                      <p>Filter by</p>  <span>Genre:</span>
                    <select onChange={this.handleChange}name="select" id="select">
                        <option value="tout" >All</option>
                        <option value="horror" >horror</option>
                        <option value="Fantastic">Fantastic</option>
                    </select>
                    <span>Gender:</span>
                    <select onChange={this.handleChange}name="select1" id="select1">
                        <option value="tout" >All</option>
                        <option value="masculine" >Masculine</option>
                        <option value="feminine">feminine</option>
                    </select>
                    </div>

                </div>
           {this.state.profil.length>0 ?(this.state.profil.filter(a=>{
               if(document.getElementById("select").options[document.getElementById("select").selectedIndex].value=="tout"&& document.getElementById("select1").options[document.getElementById("select1").selectedIndex].value=="tout"){
                   return a
               }
               else if(document.getElementById("select").options[document.getElementById("select").selectedIndex].value=="tout"&& document.getElementById("select1").options[document.getElementById("select1").selectedIndex].value!=="tout"){
                return  a.author.gender===document.getElementById("select1").options[document.getElementById("select1").selectedIndex].value
               }
               else if(document.getElementById("select").options[document.getElementById("select").selectedIndex].value!=="tout"&& document.getElementById("select1").options[document.getElementById("select1").selectedIndex].value=="tout"){
                return a.genre===document.getElementById("select").options[document.getElementById("select").selectedIndex].value  
               }
               else{
                return (a.genre===document.getElementById("select").options[document.getElementById("select").selectedIndex].value && a.author.gender===document.getElementById("select1").options[document.getElementById("select1").selectedIndex].value)
               }
               }).map(prof=>{   
      return(
  <div id="atelier"> 
      <div class=" cardbody card-body">
             <p class="card-text"><strong>Title :</strong> {prof.name}</p>
             <p class="card-text"><strong>Author:</strong>{prof.author.name}</p>
             <p class="card-text"><strong>Gender:</strong>{prof.author.gender}</p>
             <p class="card-text"><strong>Genre :</strong>{prof.genre}</p>
             <p class="card-text"><strong>Publish at  :</strong>{prof.date}</p>


  </div > 

     </div>
      )})):""} 
           </div>

    </div>


        )
    }
}
